var series = [];
var currencies = _.groupBy(trades, function(item) {
    return item.instrument;
});
_.each(currencies, function(items, instrument) {
    // Keep the closed trades only
    items = _.filter(items, function(item) {
        return item.close_time;
    });
    var serie = {
        name: instrument,
        data: _.map(items, function(item) {
            return {
                x:  item.close_time,
                y:  item.pl
            }
        })
    }
    series.push(serie);
});
var formatted = utils.formatter(series, {
    group:      'sum',
    transform:  'sum'
});
var chart = {
    type:       "chart",
    chartType:   "area",
    series:     formatted.series,
    x_axis:     formatted.x_axis,
    height:     500
}
return chart;

