var series = [];

var pairs = _.groupBy(trades, function(item) {
    return item.instrument;
})

_.each(pairs, function(items, instrument) {
    // Keep the closed trades only
    items = _.filter(items, function(item) {
        return item.close_time;
    });
    
    var sum = 0;
    _.each(items, function(item) {
        sum	+= item.pl;
    })
    series.push({
    	label:	instrument,
    	value:	sum
    });
});



var chart = {
    type:       "chart",
    chartType:   "bar",
    series:		series,
}
return chart;

